import { useState, useEffect } from 'react'
import Select from './Select'
import Input from './Input'
import { useForm } from '../useForm'
import '../styles/form.css'
import { newPollFields, updatePollFields } from '../helpers.js'

export default function PollForm({ type }){
    const [fields,setFields] = useState([]);
    const [options,setOptionsTotal] = useState(2);

    const {
      handleSubmit,
      handleChange,
      data,
      errors,
    } = useForm({
      validations: { name: { required: true } },
      onSubmit: () => {console.log('submitted')}
    })

    useEffect(()=>{
      type === 'newPoll' ?
          setFields(newPollFields) : setFields(updatePollFields)
      },[])

    useEffect(()=> {
      if(options < 9 && options > 2){
        setFields([...fields,{
          type: 'text',
          focus: true,
          name:`option-${options}`,
          label: `Option ${options}`}])
      }
    },[options])

    const increaseOptions = async () => {
      if(options < 9) setOptionsTotal(options + 1)
    }

    return(
      <div className='form' onSubmit={handleSubmit}>
        { type === 'newPoll'  ?  (
          <span>
            <button onClick={increaseOptions}>Add Option</button>
            <button type='submit'>Submit Form</button>
          </span>
        ) : ''}
        {fields.map(x => {
          return (
            x.type === 'select' ?
              <Select
                key={x.name}
                props={x}
                handleChange={handleChange} />
            :
              <Input
                key={x.name}
                props={x}
                handleChange={handleChange} />)
        })}
      </div>
    )
}
