export default function Input({ props, handleChange }){
  return(
    <div className='input-group'>
      <label htmlFor={props.name}>{props.label}</label>
      <select name={props.name} autoFocus={props.focus} onChange={handleChange}>
        {props.options.map(x => {
          return <option key={x} value={x}>{x.toUpperCase()}</option>
        })}
      </select>
    </div>
  )
}
