export default function Input({ props, decreaseOptions }){
  return(
    <>
      <label htmlFor={props.name}>{props.label}</label>
      <input type={props.type} name={props.name} />
      {props.name.includes('option') ? <span onClick={decreaseOptions(props.name)}> X </span> : ''}
    </>
  )
}
