import { useState, useEffect } from 'react'
import Select from './Select'
import Input from './Input'
import '../styles/form.css'
import { newPollFields, updatePollFields } from '../helpers.js'

export default function PollForm({ type }){
    const [fields,setFields] = useState([]);
    const [formData, setFormData] = useState({});
    const [options,setOptionsTotal] = useState(2);

    useEffect(()=>{
      type === 'newPoll' ?
          setFields(newPollFields) : setFields(updatePollFields)
      },[])

    useEffect(()=> {
      if(options < 9 && options > 2){
        setFields([...fields,{
          type: 'text', 
          focus: true,
          name:`option${options}`,
          label: `Option ${options}`}])
      }
    },[options])

    useEffect(()=> {
      console.log(formData)
    },[formData])

    const increaseOptions = async () => {
      if(options < 9) setOptionsTotal(options + 1)
    }

    const handleChange = (e) => {
      const {name, value} = e.target
      setFormData({...formData, [name] :value})
    }


    return(
      <div className='form'>
        { type === 'newPoll'  ?  (
          <span>
            <button onClick={increaseOptions}>Add Option</button>
          </span>
        ) : ''}
        {fields.map(x => {
          return (
            x.type === 'select' ?
              <Select
                key={x.name}
                props={x}
                handleChange={handleChange} />
            :
              <Input
                key={x.name}
                props={x}
                handleChange={handleChange} />)
        })}
      </div>
    )
}
