import Select from './Select'
import Input from './Input'
import '../styles/form.css'
import { newPollFields, updatePollFields } from '../helpers.js'

import { useState, useEffect } from 'react'
import { useForm, useFieldArray } from 'react-hook-form'
import { yupResolver } from '@hookform/resolvers/yup';
import * as Yup from 'yup';

export default function PollForm({ type }){
    const [formFields,setFields] = useState([]);
    const [options,setOptionsTotal] = useState(2);

    const validationSchema = Yup.object().shape({
      subject: Yup.string().required('Subject is required'),
      password: Yup.string().required('Password is required').min(6, 'Password must be 6 characters'),
      confirmPassword: Yup.string().required('Confirm Password is required').oneOf([Yup.ref('password')], 'Passwords must match'),
      format: Yup.string().default('default'),
      expires: Yup.number().positive().integer().default(5).min(1).max(300),
      options: Yup.array().of(
        Yup.object().shape({
          description: Yup.string()
        })
      )
    });
    const formOptions = {resolver: yupResolver(validationSchema)}

    const {
      handleSubmit,
      register,
      control,
      reset,
      formState
    } = useForm(formOptions)

    const { errors } = formState;

    const { fields, append, remove } = useFieldArray({name: 'options', control})


    useEffect(()=>{
      console.log(errors)
      },[errors])

    useEffect(()=>{
      type === 'newPoll' ?
          setFields(newPollFields) : setFields(updatePollFields);
      },[])

    useEffect(() => {
       // update field array when ticket number changed
       const oldVal = fields.length;
       if (options > oldVal && oldVal < 8 || oldVal < 2) {
           // append tickets to field array
           for (let i = oldVal; i < options; i++) {
               append({description: ''});
           }
       } else {
           // remove tickets from field array
           for (let i = oldVal; i > options; i--) {
               remove(i - 1);
           }
       }
    }, [options]);

    const increaseOptions = (e) => {
      e.preventDefault();
      if(options < 8) setOptionsTotal(options + 1)
    }

    const decreaseOptions = (e) => {
      e.preventDefault();
      if(options > 2) setOptionsTotal(options - 1)
    }

    const onSubmit = (data) => {
      console.log(data)
    }

    return(
      <form className='form' onSubmit={handleSubmit(onSubmit)}>
        { type === 'newPoll'  ?  (
          <span>
            <button onClick={increaseOptions}>Add Option</button>
            <button onClick={decreaseOptions}>Delete Option</button>
            <button type='submit'>Submit Form</button>
          </span>
        ) : ''}
        {
          errors ? Object.keys(errors).map(err =>{
            for(let field in err){
              const { message } = field
              return (
                <p>{message}</p>
              )
            }
        }) : ''

      }
        {formFields.map(x => {
          return (
            x.type === 'select' ?
              <Select
                key={x.name}
                props={x}
                register={register} />
            :
              <Input
                key={x.name}
                props={x}
                register={register} />)
        })}
        {fields.map((x,i) => {
          return (
            <Input
              key={x.id}
              props={{
                type: 'text',
                focus: true,
                name:`options.${i}.description`,
                label: `Option ${i+1}`}}
              register={register} />
          )
        })}
      </form>
    )
}
